mapper.select:
		SELECT * FROM (
			SELECT ROWNUM rnum, tb.* FROM (
				SELECT c.userId, userName, c.num, courseName, content,
                price, TO_CHAR(c.reg_date, 'YYYY-MM-DD') creg_date, c.enabled, category, 
                chapterNum, chapterName, saveFilename, lessonName, lessonNum, TO_CHAR(v.reg_date, 'YYYY-MM-DD') vreg_date, state 
				FROM course c
				JOIN member1 m1 ON c.userId=m1.userId
				JOIN courseCategory cc ON c.categoryNum=cc.categoryNum
				LEFT OUTER JOIN chapter chp ON c.num=chp.courseNum
				LEFT OUTER JOIN video v ON chp.chapNum=v.chapNum
				
					
						
					
					
						AND enabled = #{enabled, jdbcType=INTEGER}
					
				 
				ORDER BY c.num
			) tb WHERE ROWNUM <= #{end}
		) WHERE rnum >= #{start}

SELECT * FROM (
	SELECT stateNum  , courseNum, stateCode, memo, registerId, sReg_date
	FROM courseState
	WHERE courseNum = #{num}
	ORDER BY stateNum   DESC
) WHERE ROWNUM = 1
