mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT w.num, w.userId, amount, price, TO_CHAR(buy_date, 'YYYY-MM-DD') buy_date
		FROM mywallet w
		JOIN member1 m ON w.userId=m.userId
		WHERE w.userId=#{userId}
		ORDER BY num DESC
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
