mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT w.num, w.userId, amount, price, buy_date, state
		FROM mywallet w
		JOIN member1 m ON w.userId=m.userId
		WHERE w.userId=#{userId}
		ORDER BY num DESC
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}

SELECT NVL(SUM(amount), 0)
FROM mywallet
WHERE userId=#{userId}

SELECT NVL(sum(amount), 0)
FROM useCookie
WHERE userId=#{userId}

SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT u.num, u.userId, amount, courseName, use_date, state
		FROM useCookie u
		JOIN member1 m ON u.userId=m.userId
		WHERE u.userId=#{userId}
		ORDER BY num DESC
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
