mapper.select:
SELECT COUNT(*) FROM member m JOIN member1 m1 ON m.memberIdx = m1.memberIdx LEFT OUTER JOIN member2 m2 ON m1.userId = m2.userId
AND enabled = #{enabled}
AND m.membership < 10

SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT m1.memberIdx, m1.userId, userName, 
			m1.register_date, m1.modify_date, last_login,
			enabled, email, tel, 
			TO_CHAR(birth, 'YYYY-MM-DD') birth
		FROM member m
		JOIN member1 m1 ON m.memberIdx = m1.memberIdx
		LEFT OUTER JOIN member2 m2 ON m1.userId = m2.userId
		
			
				
			
			
				AND enabled = #{enabled}
			
			AND m.membership < 10
		 
		ORDER BY register_date DESC
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}

SELECT * FROM (
	SELECT stateNum  , userId, stateCode, memo, registerId, reg_date
	FROM memberState
	WHERE userId = #{userId}
	ORDER BY stateNum   DESC
) WHERE ROWNUM = 1
