mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT num, subject, content, TO_CHAR(reg_date, 'YYYY-MM-DD') reg_date, userName, price, type, hitCount
	FROM trade s
	JOIN member1 m ON s.userId=m.userId
	
		
			
		
	
	ORDER BY num DESC
	) tb WHERE ROWNUM <=#{end}
) WHERE rnum >=#{start}

mapper.update:
UPDATE trade SET subject=#{subject}, content=#{content}, type=#{type}, price=#{price}, saveFilename=#{saveFilename, jdbcType=VARCHAR},
		originalFilename=#{originalFilename, jdbcType=VARCHAR}
WHERE num=#{num}

mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, tb.* FROM (
		SELECT r.replyNum, r.userId, userName, num, content, r.reg_date,
			NVL(answerCount, 0) answerCount
		FROM tradeReply r
		JOIN member1 m ON r.userId = m.userId
		LEFT OUTER JOIN(
			SELECT answer, COUNT(*) answerCount
			FROM tradeReply WHERE answer!=0 GROUP BY answer
		) a ON r.replyNum = a.answer
		WHERE num=#{num} AND r.answer=0
		ORDER BY r.replyNum DESC
	) tb WHERE ROWNUM <= #{end}
) WHERE rnum >=#{start}

mapper.delete:
DELETE FROM tradeReply
WHERE replyNum=#{replyNum}

	OR answer=#{replyNum}
